INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'ASP.NET Core MVC с примерами на C# для профессионалов', N'В книге объясняется, как эффективно применять новые возможности инфраструктуры "модель-представление-контроллер" (MVC), обновленной до версии ASP.NET Core MVC. Теперь вы сможете создавать более экономные, оптимизированные под облако и готовые к функционированию на мобильных устройствах приложения для платформы .NET. Книга предоставляет детальное описание того, как вписать новую функциональность в существующий контекст разработки.', CAST(3000.00 AS Decimal(16, 2)), NULL, NULL, 2, 1, N'/Content/images/Pro AspNet Core MVC.jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'ASP.NET MVC 5 Framework с примерами на C#', N'ASP.NET MVC 3 Framework представляет собой последнюю версию веб-платформы ASP.NET от Microsoft. Она предлагает высокопроизводительную модель программирования, которая способствует построению более чистой кодовой архитектуры, обеспечивает разработку, управляемую тестами, и позволяет повсеместную расширяемость в комбинации со всеми преимуществами версии ASP.NET 4.', CAST(3700.00 AS Decimal(16, 2)), NULL, NULL, 2, 1, N'/Content/images/Pro ASP.NET MVC 5.jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'ASP.NET MVC 5 - Построение сайта с использованием Visual Studio 2015 и C Sharp: Тактическая книга', N'Изучите актуальные вопросы безопасности и развертывания, включая особенности использования IIS 7
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'Построение динамичных, мобильных приложений на ASP.NET MVC 5', N'Платформа ASP.NET Core MVC предлагает "независимую от хоста" инфраструктуру и высокопродуктивную модель программирования, которая способствует построению более чистой кодовой архитектуры, разработке через тестирование и значительной расширяемости.', CAST(3900.00 AS Decimal(16, 2)), NULL, NULL, 2, 1, N'/Content/images/ASP.NET MVC 5 with Bootstrap and Knockout.jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'Профессиональная разработка в ASP.NET MVC 5', N'Он представляет полностью работающий учебный пример функционирующего приложения ASP.NET MVC, который вы сможете использовать в качестве шаблона для собственных проектов. Вы начнете с азов и постепенно доберетесь до описания более сложных средств.', CAST(5500.00 AS Decimal(16, 2)), NULL, NULL, 2, 1, N'/Content/images/Professional ASP.NET MVC 5.jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'Язык программирования C# 6.0 и платформа .NET 4.6', N'овое 7-е издание этой книги было полностью пересмотрено и переписано с учетом последних изменений спецификации языка C# и новых достижений платформы .NET Framework. Отдельные главы посвящены важным новым средствам, которые делают .NET Framework 4.6 самым передовым выпуском', CAST(4500.00 AS Decimal(16, 2)), NULL, NULL, 1, 1, N'/Content/images/C Sh 6.0 and the .NET 4.6 Framework.jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'C# для профессионалов. Тонкости программирования', N'Книга C# ДЛЯ ПРОФЕССИОНАЛОВ. ТОНКОСТИ ПРОГРАММИРОВАНИЯ (C# in Depth) является обновлением предыдущего издания, ставшего бестселлером, с целью раскрытия новых средств языка C# 5, включая решение проблем, которые связаны с написанием сопровождаемого асинхронного кода.', CAST(4200.00 AS Decimal(16, 2)), NULL, NULL, 1, 1, N'/Content/images/C Sh in Depth, 3rd Edition.jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'Эффективная разработка на C# (Включая C# 6.0)', N'Помимо этого предлагается исчерпывающее рассмотрение всех ключевых возможностей языка C#, как старых, так и новых, что позволило обрести популярность предыдущим изданиям этой книги. Читатели получат основательные знания приемов объектно-ориентированной обработки, атрибутов и рефлексии, обобщений и коллекций.', CAST(4700.00 AS Decimal(16, 2)), NULL, NULL, 1, 1, N'/Content/images/Effective C Sh (Covers CSh 6.0).jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'C# 6.0. Справочник. Полное описание языка', N'Данное руководство, ставшее бестселлером, позволяет получить точные ответы практически на любые вопросы по C# 6.0, CLR и основным сборкам .NET Framework. После своего появления в 2000 году C# стал языком с выдающейся гибкостью и мощью, но его постоянный рост и совершенствование означают необходимость в дополнительном изучении.', CAST(4500.00 AS Decimal(16, 2)), NULL, NULL, 1, 1, N'/Content/images/C Sh 6.0 in a Nutshell The Definitive Reference.jpg')
INSERT INTO [dbo].[Products] ( [Name], [Description], [Price], [ImageData], [ImageMimeType], [CategoryId], [TypeId], [ImageUrl]) VALUES ( N'C# 6 для программистов (6th Издание) ( Дейтель)', N'Основная миссия книги заключается в том, чтобы служить исчерпывающим руководством по языку программирования C# и ключевым аспектам платформы .NET, а также предоставлять обзорные сведения о технологиях, построенных на основе C# и .NET (ADO.NET и Entity Framework, Windows Communication Foundation (WCF), Windows Presentation Foundation (WPF) и ASP.NET (Web Forms, MVC, Web API)).', CAST(3500.00 AS Decimal(16, 2)), NULL, NULL, 1, 1, N'/Content/images/C Sh 6 for Programmers (6th Edition) (Deitel Developer Series).jpg')
